(()=>{const e=document.getElementById("toggle"),t=document.getElementById("overlay"),s=document.getElementById("nav"),a=document;e.addEventListener("click",(()=>{e.classList.toggle("menu"),t.classList.toggle("overlay-active"),s.classList.toggle("nav-active")})),a.addEventListener("click",(a=>{a.target.matches(".menuelemt")&&(e.classList.remove("menu"),t.classList.remove("overlay-active"),s.classList.remove("nav-active"))}))})();